import 'dart:convert';

import 'package:camera/camera.dart';
import 'package:flutter/foundation.dart';
import 'package:flutter/material.dart';
import 'package:http/http.dart' as http;

class Scan extends StatefulWidget {
  const Scan({Key? key}) : super(key: key);

  @override
  State<Scan> createState() => _ScanState();
}

class _ScanState extends State<Scan> {
  late List<CameraDescription> cameras;
  late CameraController cameraController;
  final TextEditingController textEditingController = TextEditingController();
  Uint8List? bytesImage = Uri.parse('data:image/png;base64,').data?.contentAsBytes();

  int direction = 0;

  @override
  void initState() {
    startCamera(direction);
    super.initState();
  }

  void startCamera(int direction) async {
    cameras = await availableCameras();

    cameraController = CameraController(
      cameras[direction],
      ResolutionPreset.high,
      enableAudio: false,
    );

    await cameraController.initialize().then((value) {
      if (!mounted) {
        return;
      }
      setState(() {}); //To refresh widget
    }).catchError((e) {
      if (kDebugMode) {
        print(e);
      }
    });
  }

  @override
  void dispose() {
    cameraController.dispose();
    super.dispose();
  }

  Image imageFromBase64String(String base64String) {
    return Image.memory(base64Decode(base64String));
  }

  Uint8List dataFromBase64String(String base64String) {
    return base64Decode(base64String);
  }

  String base64String(Uint8List data) {
    return base64Encode(data);
  }

  @override
  Widget build(BuildContext context) {

    if (cameraController.value.isInitialized) {
      return Scaffold(
        body: Stack(
          children: [
            CameraPreview(cameraController),
            Padding(
              padding: const EdgeInsets.all(8.0),
              child: Column(
                children: [
                  const Spacer(),
                  Image.memory(bytesImage!),
                  Center(
                    child: FloatingActionButton(
                      child: const Icon(Icons.camera_alt_outlined),
                      onPressed: () {
                        cameraController
                            .takePicture()
                            .then((XFile? file) async {
                          if (mounted) {
                            if (file != null) {
                              Uri uri = Uri.parse(
                                  'http://${textEditingController.text}/api/cible/uploadCibleSmartphone');

                              http.Response response = await http.post(
                                uri,
                                headers: <String, String>{
                                  'Content-Type':
                                      'application/json; charset=UTF-8',
                                },
                                body: await file.readAsBytes(),
                              );
                              var responseString = await response.body;

                              if (kDebugMode) {
                                print(response.body);
                              }

                              final UriData? data = Uri.parse(responseString).data;

                              setState(() {
                                bytesImage = data?.contentAsBytes();
                              });
                            }
                          }
                        });
                      },
                    ),
                  ),
                  TextField(
                    controller: textEditingController,
                    decoration: const InputDecoration(
                        border: OutlineInputBorder(),
                        hintText: '192.168.1.20:8080'),
                  )
                ],
              ),
            )
          ],
        ),
      );
    } else {
      return const SizedBox();
    }
  }

  Widget button(IconData icon, Alignment alignment) {
    return Align(
      alignment: alignment,
      child: Container(
        margin: const EdgeInsets.only(
          left: 20,
          bottom: 20,
        ),
        height: 50,
        width: 50,
        decoration: const BoxDecoration(
          shape: BoxShape.circle,
          color: Colors.white,
          boxShadow: [
            BoxShadow(
              color: Colors.black26,
              offset: Offset(2, 2),
              blurRadius: 10,
            ),
          ],
        ),
        child: Center(
          child: Icon(
            icon,
            color: Colors.black54,
          ),
        ),
      ),
    );
  }
}
